import React, { Component } from 'react';
class Card extends Component {
  render() {
    // ...
    // return (
    //     <div class="card">
    //         <img src="img/s22.jpg" class="img" alt=""/>
    //         <div class="content">
    //             <i style="background-color:#ebdc9c;"></i>
    //             <h2>Brand Design <span style="color:#ebdc9c"> + </span><br/>Innovation</h2>
    //             <i style="background-color:#ebdc9c;"></i>
    //             <h5>Read More</h5>
    //         </div>
    //     </div>
    // );
    const cards=[{color:'#ebdc9c',src:'img/s22.jpg'},{color:'#ebdc9c',src:'img/s21.png'},
    {color:'#ebdc9c',src:'img/s23.png'},{color:'#ebdc9c',src:'img/s24.jpg'}];
    // const data =[{"name":"test1"},{"name":"test2"}];
    const cardItems = cards.map((card) =>
        <div key={card.src} className="card">
            <img src={card.src} className="img" alt=""/>
            <div className="content">
                <i style={{backgroundColor:card.color}}></i>
                <h2>Brand Design <span style={{color:card.color}}> + </span><br/>Innovation</h2>
                <i style={{backgroundColor:card.color}}></i>
                <h5>Read More</h5>
            </div>
        </div>
    );


    return (
      <div className="cards">

        {cardItems}

      </div>
    );
  }
}
export default Card; // Don’t forget to use export default!
